d); ?&#62;<br />\n      &#60;?php<br />\nwhile(!$catid==0) {	/* すべてのカテゴリーIDを取得し配列にセットするループ */<br />\n    $mycat = get_category($catid); 	/* カテゴリーIDをセット */<br />\n    $catid = $mycat-&#62;parent; 	/* 上で取得したカテゴリーIDの親カテゴリーをセット */<br />\n    array_push($allcats, $catid);<br />\n}<br />\narray_pop($allcats);<br />\n$allcats = array_reverse($allcats);<br />\n?&#62;<br />\n      &#60;?php /*--- 親カテゴリーがある場合は表示させる --- */ ?&#62;<br />\n      &#60;?php foreach($allcats as $catid): ?&#62;<br />\n      &#60;div itemscope itemtype="http://data-vocabulary.org/Breadcrumb"&#62; <a>" itemprop="url"&#62; &#60;span itemprop="title"&#62;&#60;?php echo get_cat_name($catid); ?&#62;&#60;/span&#62; </a> &#62; &#60;/div&#62;<br />\n      &#60;?php endforeach; ?&#62;<br />\n    &#60;/div&#62;<br />\n    &#60;!--/kuzu--&#62;<br />\n          &#60;!--ループ開始--&#62;<br />\n          &#60;h2&#62;「<br />\n            &#60;?php if( is_category() ) { ?&#62;<br />\n            &#60;?php single_cat_title(); ?&#62;<br />\n            &#60;?php } elseif( is_tag() ) { ?&#62;<br />\n            &#60;?php single_tag_title(); ?&#62;<br />\n            &#60;?php } elseif( is_tax() ) { ?&#62;<br />\n            &#60;?php single_term_title(); ?&#62;<br />\n            &#60;?php } elseif (is_day()) { ?&#62;<br />\n            日別アーカイブ：&#60;?php echo get_the_time(''Y年m月d日''); ?&#62;<br />\n            &#60;?php } elseif (is_month()) { ?&#62;<br />\n            月別アーカイブ：&#60;?php echo get_the_time(''Y年m月''); ?&#62;<br />\n            &#60;?php } elseif (is_year()) { ?&#62;<br />\n            年別アーカイブ：&#60;?php echo get_the_time(''Y年''); ?&#62;<br />\n            &#60;?php } elseif (is_author()) { ?&#62;<br />\n            投稿者アーカイブ：&#60;?php echo esc_html(get_queried_object()-&#62;display_name); ?&#62;<br />\n            &#60;?php } elseif (isset($_GET[''paged'']) &#38;&#38; !empty($_GET[''paged''])) { ?&#62;<br />\n              ブログアーカイブ<br />\n              &#60;?php } ?&#62;<br />\n            」 一覧 &#60;/h2&#62;<br 